// This is the payload structure for the SSO token
export interface SsoTokenPayload {
  iss: string; // Distribution partner ID
  iat?: number; // Issued at
  exp?: number; // Expires at
  user: {
    id?: string;
    firstName: string;
    lastName: string;
    email: string;
    phone?: string;
  };
  business: {
    id?: string;
    name: string;
    addressLine1: string;
    addressLine2?: string;
    city: string;
    state: string;
    zip: string;
    country: string;
    phone?: string;
    email: string;
  };
}

export interface RampartClientConfig {
  secret: string; // The secret key for signing
  environment?: "production" | "development"; // The environment to use
}
